import * as cornerstone from '@cornerstonejs/core';
import * as cornerstoneTools from '@cornerstonejs/tools';
import * as cornerstoneDICOMImageLoader from '@cornerstonejs/dicom-image-loader';

export function initializeDICOMImageLoader() {
  // Configure cornerstone DICOM image loader
  cornerstoneDICOMImageLoader.external.cornerstone = cornerstone;
  cornerstoneDICOMImageLoader.external.cornerstoneTools = cornerstoneTools;

  // Set up image loader configuration
  const config = {
    maxWebWorkers: navigator.hardwareConcurrency || 1,
    startWebWorkersOnDemand: true,
  };
  cornerstoneDICOMImageLoader.webWorkerManager.initialize(config);
}

export async function loadDicomImage(imageId) {
  return cornerstone.imageLoader.loadAndCacheImage(imageId);
}
```

