import { setupRenderingEngine } from '../core/renderingEngine';
import { initializeViewport } from '../core/viewport';
import { loadDicomImage } from '../core/imageLoader';

export class DicomViewer {
  private element: HTMLElement;
  private renderingEngine: any;
  private viewport: any;

  constructor(element: HTMLElement) {
    this.element = element;
    this.initialize();
  }

  private async initialize() {
    this.renderingEngine = setupRenderingEngine();
    this.viewport = initializeViewport(this.element);
  }

  public async displayImage(imageId: string) {
    const image = await loadDicomImage(imageId);
    this.viewport.displayImage(image);
    this.renderingEngine.render();
  }
}
```

