```typescript
import * as cornerstone from '@cornerstonejs/core';

export function create3DViewport(renderingEngine, viewportId, element, options = {}) {
  const viewport = renderingEngine.enableElement({
    viewportId,
    type: cornerstone.Enums.ViewportType.VOLUME_3D,
    element,
    defaultOptions: {
      orientation: cornerstone.Enums.OrientationAxis.CORONAL,
      background: [0, 0, 0],
      ...options
    },
  });
  return viewport;
}

export function setupVolumeViewport(viewport, volume, renderingOptions = {}) {
  viewport.setVolumes([
    {
      volumeId: volume.volumeId,
      callback: ({ volumeActor }) => {
        // Configure volume actor properties
        volumeActor.getProperty().setInterpolationTypeToLinear();
        volumeActor.getProperty().setShade(true);
        volumeActor.getProperty().setSpecular(0.5);
        volumeActor.getProperty().setSpecularPower(15);
        // Apply any custom rendering options
        Object.assign(volumeActor, renderingOptions);
      },
    },
  ]);
}
```

3. Implement MPR functionality (src/core/mprViewport.ts):
```typescript
import * as cornerstone from '@cornerstonejs/core';

export function createMPRViewports(renderingEngine, containerElements, options = {}) {
  const axialId = 'AXIAL';
  const sagittalId = 'SAGITTAL';
  const coronalId = 'CORONAL';

  const viewportIds = [axialId, sagittalId, coronalId];
  const viewportTypes = Array(3).fill(cornerstone.Enums.ViewportType.ORTHOGRAPHIC);
  const orientations = [
    cornerstone.Enums.OrientationAxis.AXIAL,
    cornerstone.Enums.OrientationAxis.SAGITTAL,
    cornerstone.Enums.OrientationAxis.CORONAL,
  ];

  renderingEngine.enableElements(viewportIds, containerElements, viewportTypes, options);

  const viewports = viewportIds.map((id, index) => {
    const viewport = renderingEngine.getViewport(id);
    viewport.setOrientation(orientations[index]);
    return viewport;
  });

  return viewports;
}

export function setupMPRViewports(viewports, volume) {
  viewports.forEach(viewport => {
    viewport.setVolumes([{ volumeId: volume.volumeId }]);
  });
}
```

