export class VolumeController {
  private volume: any;
  private renderingEngine: any;

  constructor(volume, renderingEngine) {
    this.volume = volume;
    this.renderingEngine = renderingEngine;
  }

  public adjustVolumeProperties(properties) {
    const { windowWidth, windowCenter, opacity, colormap } = properties;

    if (windowWidth && windowCenter) {
      this.volume.windowWidth = windowWidth;
      this.volume.windowCenter = windowCenter;
    }

    if (opacity !== undefined) {
      this.volume.opacity = opacity;
    }

    if (colormap) {
      this.volume.colormap = colormap;
    }

    this.renderingEngine.render();
  }
}
