import * as cornerstoneTools from '@cornerstonejs/tools';

export function customizeAnnotationStyle(toolName, styleOptions) {
  const configuration = cornerstoneTools.getToolConfiguration(toolName) || {};

  // Apply style options
  const updatedConfig = {
    ...configuration,
    ...styleOptions
  };

  // Update tool configuration
  cornerstoneTools.setToolConfiguration(toolName, updatedConfig);
}

export function setGlobalAnnotationStyle(styleOptions) {
  // Set global tool style
  const globalConfig = {
    color: styleOptions.color || 'rgb(255, 255, 0)',
    lineWidth: styleOptions.lineWidth || 2,
    font: styleOptions.font || '15px Arial',
    textBackgroundColor: styleOptions.textBackgroundColor || 'rgba(0, 0, 0, 0.8)',
    textColor: styleOptions.textColor || 'rgb(255, 255, 255)',
  };

  cornerstoneTools.setToolConfiguration(null, globalConfig);
}
