import * as cornerstone from '@cornerstonejs/core';

export class WindowLevelController {
  private viewport;

  constructor(viewport) {
    this.viewport = viewport;
  }

  public setWindowLevel(windowWidth, windowCenter) {
    this.viewport.setProperties({
      voiRange: {
        windowWidth,
        windowCenter
      }
    });
    this.viewport.render();
  }

  public getWindowLevel() {
    const properties = this.viewport.getProperties();
    return properties.voiRange;
  }

  public resetWindowLevel() {
    // Reset to default window/level
    const image = this.viewport.getCurrentImage();
    if (image) {
      this.setWindowLevel(image.windowWidth, image.windowCenter);
    }
  }
}
