import { windowLevelPresets, getPresetByName } from '../config/windowLevelPresets';

export class WindowLevelPanel {
  private element;
  private windowLevelController;

  constructor(containerElement, windowLevelController) {
    this.element = containerElement;
    this.windowLevelController = windowLevelController;
    this.render();
    this.attachEventListeners();
  }

  private render() {
    // Create preset buttons
    const presetNames = Object.keys(windowLevelPresets);

    let html = '<div class="window-level-panel">';
    html += '<h3>Window/Level Presets</h3>';
    html += '<div class="preset-buttons">';

    presetNames.forEach(name => {
      html += `<button data-preset="${name}">${name}</button>`;
    });

    html += '</div>';
    html += '<div class="manual-controls">';
    html += '<label>Window Width: <input type="range" id="windowWidth" min="1" max="4000" step="1"></label>';
    html += '<label>Window Center: <input type="range" id="windowCenter" min="-1000" max="1000" step="1"></label>';
    html += '</div>';
    html += '<button id="resetWindowLevel">Reset</button>';
    html += '</div>';

    this.element.innerHTML = html;
  }

  private attachEventListeners() {
    // Preset button clicks
    const presetButtons = this.element.querySelectorAll('[data-preset]');
    presetButtons.forEach(button => {
      button.addEventListener('click', () => {
        const presetName = button.getAttribute('data-preset');
        const preset = getPresetByName(presetName);
        this.windowLevelController.setWindowLevel(preset.windowWidth, preset.windowCenter);
        this.updateInputs(preset.windowWidth, preset.windowCenter);
      });
    });

    // Manual slider inputs
    const windowWidthInput = this.element.querySelector('#windowWidth');
    const windowCenterInput = this.element.querySelector('#windowCenter');

    windowWidthInput.addEventListener('input', () => {
      const windowWidth = parseInt(windowWidthInput.value, 10);
      const { windowCenter } = this.windowLevelController.getWindowLevel();
      this.windowLevelController.setWindowLevel(windowWidth, windowCenter);
    });

    windowCenterInput.addEventListener('input', () => {
      const windowCenter = parseInt(windowCenterInput.value, 10);
      const { windowWidth } = this.windowLevelController.getWindowLevel();
      this.windowLevelController.setWindowLevel(windowWidth, windowCenter);
    });

    // Reset button
    const resetButton = this.element.querySelector('#resetWindowLevel');
    resetButton.addEventListener('click', () => {
      this.windowLevelController.resetWindowLevel();
      const { windowWidth, windowCenter } = this.windowLevelController.getWindowLevel();
      this.updateInputs(windowWidth, windowCenter);
    });
  }

  private updateInputs(windowWidth, windowCenter) {
    const windowWidthInput = this.element.querySelector('#windowWidth');
    const windowCenterInput = this.element.querySelector('#windowCenter');

    windowWidthInput.value = windowWidth;
    windowCenterInput.value = windowCenter;
  }
}
```

