export class ZoomControls {
  private element;
  private viewport;

  constructor(containerElement, viewport) {
    this.element = containerElement;
    this.viewport = viewport;
    this.render();
    this.attachEventListeners();
  }

  private render() {
    let html = '<div class="zoom-controls">';
    html += '<button id="zoomIn">+</button>';
    html += '<button id="zoomOut">-</button>';
    html += '<button id="zoomReset">Reset</button>';
    html += '</div>';

    this.element.innerHTML = html;
  }

  private attachEventListeners() {
    const zoomInButton = this.element.querySelector('#zoomIn');
    const zoomOutButton = this.element.querySelector('#zoomOut');
    const zoomResetButton = this.element.querySelector('#zoomReset');

    zoomInButton.addEventListener('click', () => {
      const properties = this.viewport.getProperties();
      const newScale = properties.scale * 1.2;
      this.viewport.setProperties({ scale: newScale });
      this.viewport.render();
    });

    zoomOutButton.addEventListener('click', () => {
      const properties = this.viewport.getProperties();
      const newScale = properties.scale / 1.2;
      this.viewport.setProperties({ scale: newScale });
      this.viewport.render();
    });

    zoomResetButton.addEventListener('click', () => {
      this.viewport.resetCamera();
      this.viewport.render();
    });
  }
}
