import * as cornerstoneTools from '@cornerstonejs/tools';

export function initializeGestureHandlers() {
  // Enable touch tools
  cornerstoneTools.addTool(cornerstoneTools.PinchZoomTool);
  cornerstoneTools.addTool(cornerstoneTools.ZoomTouchPinchTool);
  cornerstoneTools.addTool(cornerstoneTools.PanMultiTouchTool);
}

export function enableTouchGestures(element) {
  // Enable pinch/zoom
  cornerstoneTools.setToolActive('ZoomTouchPinch', { });

  // Enable multi-touch pan
  cornerstoneTools.setToolActive('PanMultiTouch', { });
}

export function handleMouseWheel(element) {
  // Add mouse wheel event listener for zooming
  element.addEventListener('wheel', (event) => {
    event.preventDefault();

    const viewport = cornerstone.getViewport(element);
    const delta = event.deltaY > 0 ? -0.1 : 0.1;
    const newScale = viewport.scale + delta;

    if (newScale > 0.1) { // Prevent zooming out too far
      viewport.scale = newScale;
      cornerstone.setViewport(element, viewport);
      cornerstone.updateImage(element);
    }
  });
}
