export class RotationController {
  private viewport;

  constructor(viewport) {
    this.viewport = viewport;
  }

  public rotateImage(degrees) {
    const properties = this.viewport.getProperties();
    const currentRotation = properties.rotation || 0;
    const newRotation = (currentRotation + degrees) % 360;

    this.viewport.setProperties({ rotation: newRotation });
    this.viewport.render();

    return newRotation;
  }

  public setRotation(degrees) {
    this.viewport.setProperties({ rotation: degrees });
    this.viewport.render();

    return degrees;
  }

  public getRotation() {
    const properties = this.viewport.getProperties();
    return properties.rotation || 0;
  }
}
