export class FlipController {
  private viewport;

  constructor(viewport) {
    this.viewport = viewport;
  }

  public flipImageHorizontal() {
    const properties = this.viewport.getProperties();
    const hflip = properties.hflip || false;

    this.viewport.setProperties({ hflip: !hflip });
    this.viewport.render();

    return !hflip;
  }

  public flipImageVertical() {
    const properties = this.viewport.getProperties();
    const vflip = properties.vflip || false;

    this.viewport.setProperties({ vflip: !vflip });
    this.viewport.render();

    return !vflip;
  }

  public getFlipState() {
    const properties = this.viewport.getProperties();
    return {
      hflip: properties.hflip || false,
      vflip: properties.vflip || false
    };
  }

  public resetOrientation() {
    this.viewport.setProperties({
      hflip: false,
      vflip: false,
      rotation: 0
    });
    this.viewport.render();
  }
}
