export class OrientationControls {
  private element;
  private rotationController;
  private flipController;

  constructor(containerElement, rotationController, flipController) {
    this.element = containerElement;
    this.rotationController = rotationController;
    this.flipController = flipController;
    this.render();
    this.attachEventListeners();
  }

  private render() {
    let html = '<div class="orientation-controls">';
    html += '<div class="rotation-controls">';
    html += '<button id="rotateLeft"> 90</button>';
    html += '<button id="rotateRight"> 90</button>';
    html += '</div>';
    html += '<div class="flip-controls">';
    html += '<button id="flipHorizontal"> Flip H</button>';
    html += '<button id="flipVertical"> Flip V</button>';
    html += '</div>';
    html += '<button id="resetOrientation">Reset</button>';
    html += '</div>';

    this.element.innerHTML = html;
  }

  private attachEventListeners() {
    const rotateLeftButton = this.element.querySelector('#rotateLeft');
    const rotateRightButton = this.element.querySelector('#rotateRight');
    const flipHorizontalButton = this.element.querySelector('#flipHorizontal');
    const flipVerticalButton = this.element.querySelector('#flipVertical');
    const resetButton = this.element.querySelector('#resetOrientation');

    rotateLeftButton.addEventListener('click', () => {
      this.rotationController.rotateImage(-90);
    });

    rotateRightButton.addEventListener('click', () => {
      this.rotationController.rotateImage(90);
    });

    flipHorizontalButton.addEventListener('click', () => {
      this.flipController.flipImageHorizontal();
