import * as cornerstone from '@cornerstonejs/core';

export class ViewportManager {
  private renderingEngine;
  private viewports = {};

  constructor(renderingEngine) {
    this.renderingEngine = renderingEngine;
  }

  public createViewport(viewportId, element, type, options = {}) {
    // Create the viewport
    const viewport = this.renderingEngine.enableElement({
      viewportId,
      type,
      element,
      defaultOptions: options
    });

    // Store the viewport reference
    this.viewports[viewportId] = viewport;

    return viewport;
  }

  public getViewport(viewportId) {
    return this.viewports[viewportId];
  }

  public getAllViewports() {
    return Object.values(this.viewports);
  }

  public removeViewport(viewportId) {
    if (this.viewports[viewportId]) {
      this.renderingEngine.disableElement(viewportId);
      delete this.viewports[viewportId];
      return true;
    }
    return false;
  }

  public resetAllViewports() {
    Object.values(this.viewports).forEach(viewport => {
      viewport.resetCamera();
      viewport.render();
    });
  }
}
