export class SynchronizationManager {
  private syncGroups = {};

  public createSyncGroup(groupId) {
    if (!this.syncGroups[groupId]) {
      this.syncGroups[groupId] = {
        viewports: [],
        active: true
      };
    }
    return this.syncGroups[groupId];
  }

  public addViewportToSyncGroup(groupId, viewport) {
    const group = this.createSyncGroup(groupId);

    // Check if viewport is already in the group
    if (!group.viewports.includes(viewport)) {
      group.viewports.push(viewport);
    }

    return group;
  }

  public removeViewportFromSyncGroup(groupId, viewport) {
    const group = this.syncGroups[groupId];
    if (group) {
      const index = group.viewports.indexOf(viewport);
      if (index !== -1) {
        group.viewports.splice(index, 1);
        return true;
      }
    }
    return false;
  }

  public enableSyncGroup(groupId) {
    const group = this.syncGroups[groupId];
    if (group) {
      group.active = true;
      return true;
    }
    return false;
  }

  public disableSyncGroup(groupId) {
    const group = this.syncGroups[groupId];
    if (group) {
      group.active = false;
      return true;
    }
    return false;
  }

  public synchronizeViewports(groupId, sourceViewport, propertyName, value) {
    const group = this.syncGroups[groupId];
    if (group && group.active) {
      group.viewports.forEach(viewport => {
        if (viewport !== sourceViewport) {
          // Create property update object
          const updateObj = {};
          updateObj[propertyName] = value;

          // Update the viewport property
          viewport.setProperties(updateObj);
          viewport.render();
        }
      });
      return true;
    }
    return false;
  }
}
