export function linkViewportsPan(viewports, syncManager) {
  const syncGroupId = 'pan-sync-' + Date.now();

  // Add all viewports to the sync group
  viewports.forEach(viewport => {
    syncManager.addViewportToSyncGroup(syncGroupId, viewport);

    // Add event listener for pan events
    viewport.element.addEventListener('cornerstonepan', event => {
      const { translation } = event.detail;

      // Synchronize pan across viewports
      syncManager.synchronizeViewports(syncGroupId, viewport, 'translation', translation);
    });
  });

  return syncGroupId;
}

