export class SeriesManager {
  private seriesList = [];
  private currentSeriesIndex = -1;

  public addSeries(series) {
    this.seriesList.push(series);

    // If this is the first series, set it as current
    if (this.currentSeriesIndex === -1) {
      this.currentSeriesIndex = 0;
    }

    return this.seriesList.length - 1; // Return the index of the added series
  }

  public removeSeries(index) {
    if (index >= 0 && index < this.seriesList.length) {
      this.seriesList.splice(index, 1);

      // Adjust current series index if needed
      if (this.currentSeriesIndex === index) {
        this.currentSeriesIndex = this.seriesList.length > 0 ? 0 : -1;
      } else if (this.currentSeriesIndex > index) {
        this.currentSeriesIndex--;
      }

      return true;
    }
    return false;
  }

  public getSeries(index) {
    return this.seriesList[index];
  }

  public getAllSeries() {
    return [...this.seriesList];
  }

  public getCurrentSeries() {
    return this.currentSeriesIndex >= 0 ? this.seriesList[this.currentSeriesIndex] : null;
  }

  public setCurrentSeries(index) {
    if (index >= 0 && index < this.seriesList.length) {
      this.currentSeriesIndex = index;
      return true;
    }
    return false;
  }

  public getSeriesCount() {
    return this.seriesList.length;
  }
}
