import * as cornerstoneTools from '@cornerstonejs/tools';

export function initializeStackNavigationTool() {
  // Register the stack scroll tool
  cornerstoneTools.addTool(cornerstoneTools.StackScrollTool);
  cornerstoneTools.addTool(cornerstoneTools.StackScrollMouseWheelTool);
}

export function enableStackNavigationTool(element, options = {}) {
  // Enable stack scrolling with mouse/touch
  cornerstoneTools.setToolActive('StackScroll', { mouseButtonMask: 1, ...options });

  // Enable mouse wheel scrolling
  cornerstoneTools.setToolActive('StackScrollMouseWheel', {});
}

export function configureStackNavigationTool(options = {}) {
  const configuration = {
    // Loop through the stack when reaching first/last image
    loop: options.loop || false,
    // Allow skipping images for faster navigation
    skipDistance: options.skipDistance || 1,
    ...options
  };

  cornerstoneTools.setToolConfiguration('StackScroll', configuration);
}

export function navigateToNextImage(element) {
  const stackScrollTool = cornerstoneTools.getToolForElement(element, 'StackScroll');
  if (stackScrollTool) {
    stackScrollTool.moveToNextImage(element);
    return true;
  }
  return false;
}

export function navigateToPreviousImage(element) {
  const stackScrollTool = cornerstoneTools.getToolForElement(element, 'StackScroll');
  if (stackScrollTool) {
    stackScrollTool.moveToPreviousImage(element);
    return true;
  }
  return false;
}

export function jumpToImage(element, imageIndex) {
  const stackState = cornerstoneTools.getToolState(element, 'stack');
  if (stackState && stackState.data && stackState.data.length > 0) {
    const stackData = stackState.data[0];

    // Ensure index is within bounds
    const validIndex = Math.max(0, Math.min(imageIndex, stackData.imageIds.length - 1));

    // Set the current index and update the image
    stackData.currentImageIdIndex = validIndex;
    cornerstone.updateImage(element);

    return validIndex;
  }
  return -1;
}
