export class SeriesBrowser {
  private element;
  private seriesManager;
  private onSeriesSelected;

  constructor(containerElement, seriesManager, onSeriesSelected) {
    this.element = containerElement;
    this.seriesManager = seriesManager;
    this.onSeriesSelected = onSeriesSelected;
    this.render();
  }

  public render() {
    const allSeries = this.seriesManager.getAllSeries();
    const currentSeriesIndex = this.seriesManager.getCurrentSeries() ? 
      allSeries.indexOf(this.seriesManager.getCurrentSeries()) : -1;

    let html = '<div class="series-browser">';
    html += '<h3>Available Series</h3>';
    html += '<ul class="series-list">';

    allSeries.forEach((series, index) => {
      const isActive = index === currentSeriesIndex;
      const activeClass = isActive ? 'active' : '';

      html += `<li class="series-item ${activeClass}" data-series-index="${index}">`;
      html += `<div class="series-thumbnail">`;
      html += `<img src="${series.thumbnail || 'placeholder.png'}" alt="Series ${index + 1}">`;
      html += `</div>`;
      html += `<div class="series-info">`;
      html += `<div class="series-description">${series.description || `Series ${index + 1}`}</div>`;
      html += `<div class="series-details">${series.imageCount || 0} Images</div>`;
      html += `</div>`;
      html += `</li>`;
    });

    html += '</ul>';
    html += '</div>';

    this.element.innerHTML = html;
    this.attachEventListeners();
  }

  private attachEventListeners() {
    const seriesItems = this.element.querySelectorAll('.series-item');

    seriesItems.forEach(item => {
      item.addEventListener('click', () => {
        const seriesIndex = parseInt(item.getAttribute('data-series-index'), 10);

        // Update current series
        this.seriesManager.setCurrentSeries(seriesIndex);

        // Update UI
        seriesItems.forEach(el => el.classList.remove('active'));
        item.classList.add('active');

        // Notify callback
        if (this.onSeriesSelected) {
          this.onSeriesSelected(this.seriesManager.getCurrentSeries(), seriesIndex);
        }
      });
    });
  }

  public update() {
    this.render();
  }
}
