export class ThumbnailViewer {
  private element;
  private series;
  private onImageSelected;
  private currentImageIndex = 0;

  constructor(containerElement, series, onImageSelected) {
    this.element = containerElement;
    this.series = series;
    this.onImageSelected = onImageSelected;
    this.render();
  }

  public render() {
    if (!this.series || !this.series.imageIds || this.series.imageIds.length === 0) {
      this.element.innerHTML = '<div class="no-images">No images available</div>';
      return;
    }

    let html = '<div class="thumbnail-viewer">';
    html += '<h3>Images</h3>';
    html += '<div class="thumbnail-grid">';

    this.series.imageIds.forEach((imageId, index) => {
      const isActive = index === this.currentImageIndex;
      const activeClass = isActive ? 'active' : '';

      html += `<div class="thumbnail ${activeClass}" data-image-index="${index}">`;
      html += `<img src="${this.series.thumbnails?.[index] || 'image-placeholder.png'}" alt="Image ${index + 1}">`;
      html += `<div class="thumbnail-number">${index + 1}</div>`;
      html += `</div>`;
    });

    html += '</div>';
    html += '<div class="thumbnail-controls">';
    html += '<button id="prevImage">Previous</button>';
    html += `<span class="image-counter">${this.currentImageIndex + 1} / ${this.series.imageIds.length}</span>`;
    html += '<button id="nextImage">Next</button>';
    html += '</div>';
    html += '</div>';

    this.element.innerHTML = html;
    this.attachEventListeners();
  }

  private attachEventListeners() {
    const thumbnails = this.element.querySelectorAll('.thumbnail');
    const prevButton = this.element.querySelector('#prevImage');
    const nextButton = this.element.querySelector('#nextImage');

    thumbnails.forEach(thumbnail => {
      thumbnail.addEventListener('click', () => {
        const imageIndex = parseInt(thumbnail.getAttribute('data-image-index'), 10);
        this.selectImage(imageIndex);
      });
    });

    if (prevButton) {
      prevButton.addEventListener('click', () => {
        if (this.currentImageIndex > 0) {
          this.selectImage(this.currentImageIndex - 1);
        }
      });
    }

    if (nextButton) {
      nextButton.addEventListener('click', () => {
        if (this.currentImageIndex < this.series.imageIds.length - 1) {
          this.selectImage(this.currentImageIndex + 1);
        }
      });
    }
  }

  public selectImage(index) {
    if (index >= 0 && index < this.series.imageIds.length) {
      this.currentImageIndex = index;

      // Update UI
      const thumbnails = this.element.querySelectorAll('.thumbnail');
      thumbnails.forEach(thumbnail => thumbnail.classList.remove('active'));

      const selectedThumbnail = this.element.querySelector(`[data-image-index="${index}"]`);
      if (selectedThumbnail) {
        selectedThumbnail.classList.add('active');
      }

      const counter = this.element.querySelector('.image-counter');
      if (counter) {
        counter.textContent = `${index + 1} / ${this.series.imageIds.length}`;
      }

      // Notify callback
      if (this.onImageSelected) {
        this.onImageSelected(this.series.imageIds[index], index);
      }

      return true;
    }
    return false;
  }

  public loadSeriesThumbnails() {
    // This would typically load thumbnails asynchronously
    // For each image in the series, generate a small thumbnail
    if (this.series && this.series.imageIds) {
      this.series.thumbnails = this.series.thumbnails || [];

      this.series.imageIds.forEach((imageId, index) => {
        // In a real implementation, you would generate thumbnails here
        // For now, we'll just use placeholders
        this.series.thumbnails[index] = 'image-placeholder.png';
      });

      this.render();
    }
  }
}
